mapper.update:
update user t


        user_name = #{userName},


        birthday = #{birthday},

    phone = #{phone},
    user_signature = #{userSignature}

where t.user_id = #{userId}

update user t
set user_password = #{userPassword}
where t.user_id = #{userId}

update user t
set  user_avatar= #{userAvatar}
where t.user_id = #{userId}
